import React from "react"

export default function Comment (props) {

  return (
    <div className='individual-comment' style={{margin: "10px auto"}}>
      <div><strong>Author: </strong>{this.props.author}</div>
      <div><strong>Message: </strong>{this.props.message}</div>
      <div><strong>Time: </strong>{this.props.message}</div>
    </div>
  )
}
