import React, { Component } from "react"
import { subscribe, stopFollow, startFollow, createDupeComment } from '../models/Comment'
import Comment from "./Comment"
import { FixedSizeList as List } from 'react-window';
import AutoSizer from "react-virtualized-auto-sizer";

class CommentList extends Component {
  constructor(props) {
    super(props)

    this.state = {
      comments: [],
      follow: true
    }

    this.handleFollow = this.handleFollow.bind(this)
    this.addCustomComment = this.addCustomComment.bind(this)
    this.handleDupeCheck = this.handleDupeCheck.bind(this)
  }

  componentDidMount() {
    this.setState({
      comments: this.props.initialComments
    })
  }

  componentDidUpdate() {
    subscribe(comment => {
      const dupeCheck = this.handleDupeCheck(comment)
      if(dupeCheck === -1) {
        this.setState({
          comments: [...this.state.comments, comment]
        })
      }
      console.log('received comment: ', comment)
    })
  }

  handleFollow() {
    if(this.state.follow) {
      stopFollow()
    } else {
      startFollow()
    }

    this.setState({
      follow: !this.state.follow
    })
  }

  addCustomComment() {
    const comment = createDupeComment()
    const dupeCheck = this.handleDupeCheck(comment)

    if(dupeCheck === -1) {
      this.setState({
        comments: [...this.state.comments, comment]
      })
    }
  }

  handleDupeCheck(val) {
    const indexOfComment = this.state.comments.findIndex((comment) => (comment.author === val.author) && (comment.message === val.message))

    return indexOfComment
  }

  render() {
    const { comments, follow } = this.state

    return (
      <div className='comment-wrapper'>
        <div>
          <button onClick={this.handleFollow}>
            {follow == true
              ? `Unfollow`
              : `Follow`}
          </button>
        </div>

        <div>
          <button onClick={this.addCustomComment}>Add Custom Comment</button>
        </div>

        <div>
          Comments array length: {comments.length}
        </div>

        {/*
        <AutoSizer>
          {({ height, width }) => (
            <List
              height={height}
              width={width}
              itemCount={this.state.comments.length}
              itemSize={50}
            >
              {({ index, style }) =>
                <Comment style={style} comment={comments[index]} key={index} />}
            </List>
          )}
        </AutoSizer>
      */}

        {comments.map((comment, index) => (
          <Comment comment={comment} key={index}/>
        ))}

      </div>
    )
  }
}

export default CommentList
