import React from "react"
import PersonIcon from '@material-ui/icons/Person';
import Grid from '@material-ui/core/Grid';

export default function Comment (props) {

  return (
    <Grid item xs={12} sm={6}>
      <div className='individual-comment' style={{margin: "10px auto", display: "flex"}}>
        <div className="avatar">
          <PersonIcon style={{fontSize: '80px'}}/>
        </div>
        <div className="commentInfo">
          <div className="authorDate">
            <div><strong>{props.comment.author}</strong></div>
            <div>Posted on {props.comment.time.toLocaleDateString("en-US")}</div>
          </div>
          <div className="message" style={{marginTop: '10px'}}>
            {props.comment.message}
          </div>
        </div>
      </div>
    </Grid>
  )
}
